<?php/** * The template for displaying the footer * * Contains the closing of the #content div and all content after. * * @link https://developer.wordpress.org/themes/basics/template-files/#template-partials * */$social_links       = get_field( 'social_links', 'options' );$social_links_title = get_field( 'social_links_title', 'options' );$copyright          = get_field( 'copyright', 'options' );?></main><!-- #main --></div><!-- #primary --></div><!-- #content --><footer id="colophon" class="site-footer overflow-hidden mt-auto py-8 lg:py-12">    <div class="container">        <div class="h-20 flex flex-wrap justify-between items-center -mx-4">            <div class="min-h-10 w-full md:w-1/2 text-primary text-center md:text-start flex justify-center md:justify-start items-center px-4">				<?php if ( ! empty( $copyright ) ) {					$copyright = str_replace( '{Y}', date( 'Y' ), $copyright );					echo $copyright;				} ?>            </div>            <div class="relative min-h-10 w-full md:w-1/2 flex justify-center md:justify-end px-4 mt-2 md:mt-0">				<?php if ( $social_links ): ?>                    <div class="social-links flex items-center justify-end gap-5 text-primary">						<?php if ( ! empty( $social_links_title ) ): ?>                            <h5 class="font-heading"><?php echo esc_html( $social_links_title ); ?></h5>						<?php endif; ?>                        <div class="flex gap-3">							<?php foreach ( $social_links as $service ): ?>                                <a href="#" target="_blank" rel="nofollow" class="text-primary hover:text-secondary transition-all duration-150">                                    <span class="text-xl"><?php echo $service['icon']; ?></span>                                    <span class="sr-only"><?php echo esc_html( $service['name'] ) ?></span>                                </a>							<?php endforeach; ?>                        </div>                    </div>				<?php endif; ?>                <div class="hidden social-media">                    <div class="social-media__text-wrap">                        <span class="social-media__icon"><i data-lucide="share-2"                                                            class="w-4 h-4 text-primary"></i></span>                        <span class="social-media__text text-sm font-bold text-primary uppercase"><?php esc_html_e( 'Follow Me', 'portfolio' ); ?></span>                    </div>                    <ul class="social-links">                        <li>                            <span class="parallax-wrap">                                <a href="#" target="_blank">In</a>                            </span>                        </li>                        <li>                            <span class="parallax-wrap">                                <a href="#" target="_blank">FB</a>                            </span>                        </li>                        <li>                            <span class="parallax-wrap">                                <a href="#" target="_blank">BE</a>                            </span>                        </li>                        <li>                            <span class="parallax-wrap">                                <a href="#" target="_blank">TW</a>                            </span>                        </li>                    </ul>                </div>            </div>        </div>    </div><!-- .container --></footer><!-- #colophon --><div id="backToTopWrapper"     class="hidden backtotop h-14 xl:flex justify-center fixed z-30 left-0 top-1/2 transition-all duration-150 -translate-y-1/2 -translate-x-[30%] -rotate-90">    <a href="#page" class="btn-backtotop flex items-center gap-x-2">        <span class="hidden xl:inline-block btn-backtotop__text text-sm text-primary font-heading font-semibold order-1"><?php esc_html_e( 'Back to top', 'portfolio' ); ?></span>        <span class="scroll-track inline-block relative h-1 w-24 bg-light-gray rounded-full order-0 overflow-hidden">            <span class="scroll-track-item inline-block bg-primary absolute h-1 bottom-0 right-0 rounded-full"></span>        </span>    </a></div></div><!-- #page --><?php wp_footer(); ?></body></html>