<?phpfunction portfolio_setup(): void {	// Add support for custom content width and wide size	add_theme_support( 'align-wide' );		// Add theme support for editor styles and custom width sizes	add_theme_support( 'editor-styles' );		// Enqueue editor stylesheet	$editor_stylesheet_path = './assets/css/style-editor.css';		add_editor_style( $editor_stylesheet_path );		add_theme_support( 'post-thumbnails' );		register_nav_menus( [		'primary' => __( 'Primary Menu', 'portfolio' ),		'footer'  => __( 'Footer Menu', 'portfolio' ),	] );}add_action( 'after_setup_theme', 'portfolio_setup' );